local m = { 
    __index = function(t, k) 
        if k == "toObject" then
            return function()  
                local o = {}
                for key, v in pairs (__key_map) do 
                    o[key] = t._raw[v]
                end
                return o
            end 
        end
        
        assert(__key_map[k], "cannot find " .. k .. " in record_knight_info")

        return t._raw[__key_map[k]]
    end
}


function knight_info.getLength()
    return #knight_info._data
end



function knight_info.hasKey(k)
  if __key_map[k] == nil then
    return false
  else
    return true
  end
end


---
--@return @class record_knight_info
function knight_info.indexOf(index)
    if index == nil or not knight_info._data[index] then
        return nil
    end
    return setmetatable({_raw = knight_info._data[index]}, m)
end



---
--@return @class record_knight_info
function knight_info.get(id)
    
    return knight_info.indexOf(__index_id[ id ])
     
end



function knight_info.set(id, key, value)
    local record = knight_info.get(id)
    if record then
        local keyIndex = __key_map[key]
        if keyIndex then
            record._raw[keyIndex] = value
        end
    end
end




function knight_info.get_index_data()
    return __index_id 
end

return  knight_info 