local __key_map = { 
    id = 1,
    num = 2,
    name = 3,
    text = 4,
}

local m = { 
    __index = function(t, k) 
        if k == "toObject" then
            return function()  
                local o = {}
                for key, v in pairs (__key_map) do 
                    o[key] = t._raw[v]
                end
                return o
            end 
        end
        
        assert(__key_map[k], "cannot find " .. k .. " in record_txt_info")

        return t._raw[__key_map[k]]
    end
}


function txt_info.getLength()
    return #txt_info._data
end



function txt_info.hasKey(k)
  if __key_map[k] == nil then
    return false
  else
    return true
  end
end


---
--@return @class record_txt_info
function txt_info.indexOf(index)
    if index == nil or not txt_info._data[index] then
        return nil
    end
    return setmetatable({_raw = txt_info._data[index]}, m)
end



---
--@return @class record_txt_info
function txt_info.get(id,num)
    
    local k = id .. '_' .. num
    return txt_info.indexOf(__index_id_num[k])
     
end



function txt_info.set(id,num, key, value)
    local record = txt_info.get(id,num)
    if record then
        local keyIndex = __key_map[key]
        if keyIndex then
            record._raw[keyIndex] = value
        end
    end
end




function txt_info.get_index_data()
    return __index_id_num 
end

return  txt_info 